/**
 * Definition for singly-linked list.
 * function ListNode(val) {
 *     this.val = val;
 *     this.next = null;
 * }
 */
/**
 * @param {ListNode} head
 * @return {ListNode}
 */
var reverseList = function(head) {
    if (head == null) {
        return null
    }
    if (head.next == null) {
        return head
    }
    var newHead = reverseList(head.next)
    head.next.next = head
    head.next = null
    return newHead
};
